mapper.select:
select
incomingPartsTime,name,
incomingPartsChannel,loanType,
management,pushStatus,
updateName
from
loans
where  1=1

    and LoanType = #{LoanType}


    and name = #{name}


    and incomingPartsChannel = #{incomingPartsChannel}


    and management = #{management}


    and status = #{status}

limit #{offset},#{pageSize}

select
count(id)
from
loans
where  1=1

    and LoanType = #{LoanType}


    and name = #{name}


    and incomingPartsChannel = #{incomingPartsChannel}


    and management = #{management}


    and status = #{status}
