mapper.select:
select rh.id,rh.merchant,rh.customerName,rh.refundAmount,rh.sendingStatus,
DATE_FORMAT(rh.createTime, '%Y-%m-%d %H:%i:%S') createTime,
DATE_FORMAT(rh.examineTime, '%Y-%m-%d %H:%i:%S') examineTime
FROM returnhandling rh



        and merchant=#{merchant}


        and customerName=#{customerName}


        and sendingStatus=#{sendingStatus}


limit #{offset},#{pageSize}

select count(*)
FROM returnhandling


        and merchant=#{merchant}


        and customerName=#{customerName}


        and sendingStatus=#{sendingStatus}
