mapper.select:
select
createTime,name,
incomingPartsChannel,loanType,
management,pushStatus,
updateName,status
FROM loans
WHERE 1=1

    and status=#{status}


    and loanType=#{loanType}


    and name=#{name}


    and merchant=#{merchant}


    and management=#{management}


    and status=#{status}


    and createTime>#{incomingPartsTimeStart}


    and #{incomingPartsTimeEnd}>createTime


    and selectType=#{selectType}

limit #{offset},#{pageSize}

select
    count(id)
FROM loans
WHERE 1=1

    and status=#{status}


    and loanType=#{loanType}


    and name=#{name}


    and merchant=#{merchant}


    and management=#{management}


    and status=#{status}


    and createTime>#{incomingPartsTimeStart}


    and #{incomingPartsTimeEnd}>createTime


    and selectType=#{selectType}
